import React, { Component } from 'react';
import { Link } from 'react-router-dom';
import axios from 'axios';

const Routine = props => (
    <tr>
        <td>{props.routine.routineName} By</td>
        <td>{props.routine.username}</td>
        <td>{props.routine.description}</td>
        <td>
        <Link to={"/add_exercise/"+props.routine._id}>add exercises</Link> |<Link to={"/edit/"+props.routine._id}>edit</Link> | <a href="#" onClick={() => {props.deleteRoutine(props.routine._id)}}>delete</a></td>
    </tr>
)

export default class Bar extends Component {
    constructor(props) {
        super(props);

        this.deleteRoutine = this.deleteRoutine.bind(this);

        this.state = {routines: []};
    }

    componentDidMount() {
        axios.get('http://localhost:5000/routines/'+this.props.data)
            .then(res => {
                this.setState({ routines: res.data })
            })
            .catch((err) => {
                console.log(err);
            })
    }
    deleteRoutine(id) {
        axios.delete('http://localhost:5000/routines/'+id)
            .then(res => console.log(res.data));
        this.setState({
            routines: this.state.routines.filter(el => el._id !== id)
        })
    }
    routineList() {
            return( <Routine routine= {this.state.routines} key={this.state.routines._id} />
            )
    }

    render() {
        return (
            <div>
                <h3>Logged Routines</h3>
                <table className="table">
                    <tr>
                        </tr>
                    <tbody>
                        {this.routineList()}
                    </tbody>
                </table>
            </div>
        )
    }
}
