import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom';

const Routine = props => (
    <tr>
        <td>{props.routine.name}</td>
        <td>{props.routine.description}</td>
        <td>
        <Link to={"/add_exercise/"+props.routine._id}>add exercises</Link> |<Link to={"/edit/"+props.routine._id}>edit</Link> | <a href="#" onClick={() => {props.deleteRoutine(props.routine._id)}}>delete</a></td>
    </tr>
)


export default class Preview extends Component {
    constructor(props) {
        super(props);

        this.state = {routines: []
        };
    }

    componentDidMount() {
        axios.get('http://localhost:5000/exercise/')
            .then(res => {
                this.setState({ routines: res.data
                })
            })
            .catch((err) => {
                console.log(err);
            })
    }

    routineList() {
        return this.state.routines.map(currentroutine => {
            return <Routine routine={currentroutine} deleteRoutine={this.deleteRoutine} key={currentroutine._id} />;
        })
    }


    render() {
        return(
            <div>
                {this.props.data}
                <table className="table">
                    <thead className="thead-light">
                        <tr>
                            <th>Username</th>
                            <th>Routine Name</th>
                            <th>Description</th>
                            <th>URL</th>
                            <th>Date</th>
                            <th>Actions</th>
                        </tr>
                    </thead>
                    <tbody>
                        {this.routineList()}
                    </tbody>
                </table>
            </div>
            
        )
    }
}
